import React, { useState, useEffect } from "react";
import { fetchMenus, addMenu, deleteMenu } from "../../api/api";
import DishesSidebar from "../DishesSidebar/DishesSidebar"; // Імпортуємо бокове меню
import "./MenusPage.css";

const MenusPage = () => {
    const [menus, setMenus] = useState([]);
    const [newMenu, setNewMenu] = useState({ id: "", dishes: [] });
    const [deleteId, setDeleteId] = useState("");

    useEffect(() => {
        fetchMenus().then(setMenus);
    }, []);

    const handleAddDish = () => {
        setNewMenu((prev) => ({
            ...prev,
            dishes: [...prev.dishes, { id: "" }],
        }));
    };

    const handleDishChange = (index, value) => {
        const updatedDishes = [...newMenu.dishes];
        updatedDishes[index].id = value;
        setNewMenu((prev) => ({ ...prev, dishes: updatedDishes }));
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewMenu((prev) => ({ ...prev, [name]: value }));
    };

    const handleAddMenu = (e) => {
        e.preventDefault();
        addMenu(newMenu).then((data) => {
            setMenus((prev) => [...prev, data]);
            setNewMenu({ id: "", dishes: [] });
        });
    };

    const handleDeleteMenu = (e) => {
        e.preventDefault();
        deleteMenu(deleteId).then(() => {
            setMenus((prev) => prev.filter((menu) => menu.id !== parseInt(deleteId, 10)));
            setDeleteId("");
        });
    };

    return (
        <div className="menus-page">
            <div className="content">
                <h2>Меню</h2>
                <table>
                    <thead>
                    <tr>
                        <th>ID Меню</th>
                        <th>Страви</th>
                        <th>Калорійність</th>
                    </tr>
                    </thead>
                    <tbody>
                    {menus.map((menu) => (
                        <tr key={menu.id}>
                            <td>{menu.id}</td>
                            <td>
                                <ul>
                                    {menu.dishes.map((dish, index) => (
                                        <li key={index}>
                                            {dish.name}:
                                            <ul>
                                                {dish.dishIngredients.map((di, subIndex) => (
                                                    <li key={subIndex}>
                                                        {di.ingredient.name} - {di.grams} г
                                                    </li>
                                                ))}
                                            </ul>
                                        </li>
                                    ))}
                                </ul>
                            </td>
                            <td>
                                {menu.dishes.reduce(
                                    (total, dish) =>
                                        total +
                                        dish.dishIngredients.reduce(
                                            (dishTotal, di) =>
                                                dishTotal + di.grams * di.ingredient.caloriesPerGram,
                                            0
                                        ),
                                    0
                                )}
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
                <div className="form-section">
                    <h3>Додати Меню</h3>
                    <form onSubmit={handleAddMenu}>
                        <input
                            type="number"
                            name="id"
                            placeholder="ID Меню"
                            value={newMenu.id}
                            onChange={handleInputChange}
                            required
                        />
                        {newMenu.dishes.map((dish, index) => (
                            <div key={index}>
                                <input
                                    type="number"
                                    placeholder="ID Страви"
                                    value={dish.id}
                                    onChange={(e) => handleDishChange(index, e.target.value)}
                                    required
                                />
                            </div>
                        ))}
                        <button type="button" onClick={handleAddDish}>
                            Додати Страву
                        </button>
                        <button type="submit">Додати Меню</button>
                    </form>
                </div>
                <div className="form-section">
                    <h3>Видалити Меню</h3>
                    <form onSubmit={handleDeleteMenu}>
                        <input
                            type="number"
                            placeholder="ID Меню"
                            value={deleteId}
                            onChange={(e) => setDeleteId(e.target.value)}
                            required
                        />
                        <button type="submit">Видалити</button>
                    </form>
                </div>
            </div>
            <DishesSidebar /> {/* Виклик бокового меню для відображення страв */}
        </div>
    );
};

export default MenusPage;
