import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { fetchDishes, fetchIngredients, addDish, deleteDish } from "../../api/api";
import "./DishesPage.css";

const DishesPage = () => {
    const [dishes, setDishes] = useState([]);
    const [ingredients, setIngredients] = useState([]);
    const [newDish, setNewDish] = useState({ id: "", name: "", dishIngredients: [] });
    const [deleteId, setDeleteId] = useState("");
    const [error, setError] = useState("");

    const navigate = useNavigate();

    useEffect(() => {
        fetchDishes()
            .then(setDishes)
            .catch(() => {
                setError("Не вдалося завантажити страви. Перевірте авторизацію.");
                navigate("/login");
            });

        fetchIngredients()
            .then(setIngredients)
            .catch(() => {
                setError("Не вдалося завантажити інгредієнти. Перевірте авторизацію.");
                navigate("/login");
            });
    }, [navigate]);

    const handleAddIngredient = () => {
        setNewDish((prev) => ({
            ...prev,
            dishIngredients: [...prev.dishIngredients, { ingredient: { id: "" }, grams: "" }],
        }));
    };

    const handleIngredientChange = (index, field, value) => {
        const updatedIngredients = [...newDish.dishIngredients];
        if (field === "ingredientId") {
            updatedIngredients[index].ingredient.id = value;
        } else if (field === "grams") {
            updatedIngredients[index].grams = value;
        }
        setNewDish((prev) => ({ ...prev, dishIngredients: updatedIngredients }));
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewDish((prev) => ({ ...prev, [name]: value }));
    };

    const handleAddDish = (e) => {
        e.preventDefault();
        addDish(newDish)
            .then((data) => {
                setDishes((prev) => [...prev, data]);
                setNewDish({ id: "", name: "", dishIngredients: [] });
                setError("");
            })
            .catch(() => {
                setError("Не вдалося додати страву. Перевірте авторизацію.");
                navigate("/login");
            });
    };

    const handleDeleteDish = (e) => {
        e.preventDefault();
        deleteDish(deleteId)
            .then(() => {
                setDishes((prev) => prev.filter((dish) => dish.id !== parseInt(deleteId, 10)));
                setDeleteId("");
                setError("");
            })
            .catch(() => {
                setError("Не вдалося видалити страву. Перевірте авторизацію.");
                navigate("/login");
            });
    };

    return (
        <div className="dishes-page">
            {error && <p className="error-message">{error}</p>}

            <div className="tables-container">
                {/* Блок для страв */}
                <div className="table-section">
                    <h2>Страви</h2>
                    <table>
                        <thead>
                        <tr>
                            <th>ID страви</th>
                            <th>Назва страви</th>
                            <th>Інгредієнти</th>
                            <th>Калорійність</th>
                        </tr>
                        </thead>
                        <tbody>
                        {dishes.map((dish) => (
                            <tr key={dish.id}>
                                <td>{dish.id}</td>
                                <td>{dish.name}</td>
                                <td>
                                    <ul>
                                        {dish.dishIngredients.map((di, index) => (
                                            <li key={index}>
                                                {di.ingredient.name} - {di.grams} г
                                            </li>
                                        ))}
                                    </ul>
                                </td>
                                <td>
                                    {dish.dishIngredients.reduce(
                                        (total, di) => total + di.grams * di.ingredient.caloriesPerGram,
                                        0
                                    )}
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>

                {/* Блок для інгредієнтів */}
                <div className="table-section">
                    <h2>Інгредієнти</h2>
                    <table>
                        <thead>
                        <tr>
                            <th>ID</th>
                            <th>Назва</th>
                            <th>Калорійність (ккал/г)</th>
                        </tr>
                        </thead>
                        <tbody>
                        {ingredients.map((ingredient) => (
                            <tr key={ingredient.id}>
                                <td>{ingredient.id}</td>
                                <td>{ingredient.name}</td>
                                <td>{ingredient.caloriesPerGram}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default DishesPage;
