import React, { useState, useEffect } from "react";
import { fetchDishes } from "../../api/api";
import "./DishesSidebar.css";

const DishesSidebar = () => {
    const [dishes, setDishes] = useState([]);

    useEffect(() => {
        fetchDishes().then(setDishes); // Завантаження страв з API
    }, []);

    return (
        <div className="sidebar">
            <h3>Страви</h3>
            <table>
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Назва</th>
                </tr>
                </thead>
                <tbody>
                {dishes.map((dish) => (
                    <tr key={dish.id}>
                        <td>{dish.id}</td>
                        <td>{dish.name}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default DishesSidebar;
