import React from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Navigation from "./components/Navigation/Navigation";
import RegistrationPage from "./components/Registration/Registration";
import LoginPage from "./components/Login/Login";
import HomePage from "./components/Home/HomePage";
import IngredientsPage from "./components/Ingredients/IngredientsPage";
import DishesPage from "./components/Dishes/DishesPage";
import MenusPage from "./components/Menu/MenusPage";
import ClientsPage from "./components/Clients/ClientsPage";
import AdminPanelPage from "./components/AdminPanelPage/AdminPanelPage"; // Імпортуємо адмін-панель

function App() {
    const isAuthenticated = !!localStorage.getItem("token"); // Перевірка авторизації
    const userRoles = JSON.parse(localStorage.getItem("roles") || "[]"); // Отримуємо ролі користувача

    const isAdmin = userRoles.some(role => role.toUpperCase() === "ADMIN"); // Приведення до верхнього регістру


    return (
        <Router>
            <Navigation />
            <Routes>
                <Route path="/" element={<HomePage />} />
                <Route path="/ingredients" element={isAuthenticated ? <IngredientsPage /> : <Navigate to="/login" />} />
                <Route path="/dishes" element={isAuthenticated ? <DishesPage /> : <Navigate to="/login" />} />
                <Route path="/menus" element={isAuthenticated ? <MenusPage /> : <Navigate to="/login" />} />
                <Route path="/clients" element={isAuthenticated ? <ClientsPage /> : <Navigate to="/login" />} />
                <Route path="/register" element={<RegistrationPage />} />
                <Route path="/login" element={<LoginPage />} />
                <Route
                    path="/admin"
                    element={isAuthenticated && isAdmin ? <AdminPanelPage /> : <Navigate to="/login" />}
                />

                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
}

export default App;
