import React, { useState, useEffect } from "react";
import { fetchClients, addClient, deleteClient, findMenuForClient } from "../../api/api";
import "./ClientsPage.css";

const ClientsPage = () => {
    const [clients, setClients] = useState([]);
    const [newClient, setNewClient] = useState({
        id: "",
        name: "",
        subscriptionEndDate: "",
        weight: "",
        height: "",
        age: "",
        favoriteIngredients: [],
        allergyIngredients: [],
    });
    const [deleteId, setDeleteId] = useState("");
    const [searchClientId, setSearchClientId] = useState("");
    const [menuResults, setMenuResults] = useState([]);

    useEffect(() => {
        fetchClients().then(setClients).catch(console.error);
    }, []);

    const handleAddClient = (e) => {
        e.preventDefault();
        addClient(newClient)
            .then((data) => {
                setClients((prev) => [...prev, data]);
                setNewClient({
                    id: "",
                    name: "",
                    subscriptionEndDate: "",
                    weight: "",
                    height: "",
                    age: "",
                    favoriteIngredients: [],
                    allergyIngredients: [],
                });
            })
            .catch(console.error);
    };

    const handleDeleteClient = (e) => {
        e.preventDefault();
        deleteClient(deleteId)
            .then(() => {
                setClients((prev) => prev.filter((client) => client.id !== parseInt(deleteId, 10)));
                setDeleteId("");
            })
            .catch(console.error);
    };

    const handleFindMenu = (e) => {
        e.preventDefault();
        findMenuForClient(searchClientId)
            .then(setMenuResults)
            .catch(console.error);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewClient((prev) => ({ ...prev, [name]: value }));
    };

    return (
        <div className="container">
            <h2>Клієнти</h2>
            <table>
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Ім'я</th>
                    <th>Дата закінчення підписки</th>
                    <th>Вага</th>
                    <th>Ріст</th>
                    <th>Вік</th>
                    <th>Калорії</th>
                </tr>
                </thead>
                <tbody>
                {clients.map((client) => (
                    <tr key={client.id}>
                        <td>{client.id}</td>
                        <td>{client.name}</td>
                        <td>{client.subscriptionEndDate}</td>
                        <td>{client.weight}</td>
                        <td>{client.height}</td>
                        <td>{client.age}</td>
                        <td>{client.recommendedCalories.toFixed(2)}</td>
                    </tr>
                ))}
                </tbody>
            </table>
            <div>
                <h3>Додати клієнта</h3>
                <form onSubmit={handleAddClient}>
                    <input name="id" value={newClient.id} onChange={handleInputChange} placeholder="ID" />
                    <input name="name" value={newClient.name} onChange={handleInputChange} placeholder="Ім'я" />
                    <input
                        name="subscriptionEndDate"
                        value={newClient.subscriptionEndDate}
                        onChange={handleInputChange}
                        type="date"
                    />
                    <input name="weight" value={newClient.weight} onChange={handleInputChange} placeholder="Вага" />
                    <input name="height" value={newClient.height} onChange={handleInputChange} placeholder="Ріст" />
                    <input name="age" value={newClient.age} onChange={handleInputChange} placeholder="Вік" />
                    <button type="submit">Додати</button>
                </form>
            </div>
            <div>
                <h3>Видалити клієнта</h3>
                <form onSubmit={handleDeleteClient}>
                    <input value={deleteId} onChange={(e) => setDeleteId(e.target.value)} placeholder="ID клієнта" />
                    <button type="submit">Видалити</button>
                </form>
            </div>
            <div>
                <h3>Знайти меню для клієнта</h3>
                <form onSubmit={handleFindMenu}>
                    <input
                        value={searchClientId}
                        onChange={(e) => setSearchClientId(e.target.value)}
                        placeholder="ID клієнта"
                    />
                    <button type="submit">Знайти меню</button>
                </form>
                {menuResults.length > 0 && (
                    <div>
                        {menuResults.map((menu) => (
                            <div key={menu.id}>
                                <h4>Меню ID: {menu.id}</h4>
                                <p>Загальна калорійність: {menu.totalCalories.toFixed(2)}</p>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default ClientsPage;
